commits.content:
@import url('https://fonts.googleapis.com/css2?family=Poppins:ital,wght@0,100;0,200;0,300;0,400;0,500;0,600;0,700;0,800;0,900;1,100;1,200;1,300;1,400;1,500;1,600;1,700;1,800;1,900&display=swap');

@font-face {
  font-family: TsunagiGothic;
  src: url("../fonts/TsunagiGothic.ttf");
}

:root {
  --primary: #faf6eb;
}

* {
  font-family: TsunagiGothic;
  box-sizing: border-box;
}

*, html {
  scroll-behavior: smooth !important;
}

html {
  overflow-x: hidden;
}

body {
  background: var(--primary);
}

img {
  user-drag: none;
  -webkit-user-drag: none;
  user-select: none;
  -moz-user-select: none;
  -webkit-user-select: none;
  -ms-user-select: none;
}

.pageWrapper {
  max-width: 1280px;
  margin: 0 auto;
  padding: 0 12px;
}

.errorPage {
font-family:

;
  height: 90vh;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  gap: 40px;

  h1 {
    font-size: 24px;
    font-weight: 900;

    span {
      color: var(--primary);
    }
  }

  a {
    span {
      font-size: 20px;
      padding-bottom: 2px;
      font-weight: 600;
      border-bottom: 1px solid #000;
    }
  }

  .logo {
    z-index: 9999;
    font-size: 64px;
    font-weight: 700;
    margin-bottom: 40px;

    span {
      color: var(--primary);
    }
  }
}

.mangaWrapper {
  .backWrapper {
    position: absolute;
    left: 50px;
    top: 50px;
    color: #fff;
    z-index: 2;
    display: flex;
    align-items: center;

    img {
      padding-right: 12px;
      padding-bottom: 2px;
    }

    h1 {
      font-size: 24px;
      font-weight: 600;
    }

    @media(max-width: 621px) {
      left: 20px;
      top: 20px;
    }
  }

  .back {
    display: flex;
    align-items: center;
  }
}

a {
  transition: 0.3s all;

  &:hover {
    opacity: 0.8;
  }
}

.policiesPage {
  height: 95vh;

  img {
    object-fit: cover;
    z-index: -1;
  }

  section {
    position: relative;
    height: 100%;
  }

  article {
    margin: 0 auto;
    width: 500px;
    color: #fff;
    padding: 200px 12px 0;

    @media (max-width: 529px) {
      width: 100%;
    }

    h2 {
      font-size: 24px;
      font-weight: 500;
      padding-bottom: 12px;
    }

    p, h2 {
      filter: drop-shadow(0px 0px 4px rgba(0, 0, 0, 0.25));
    }
  }
}
